package com.training.examples1;

public class EvenNo 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int Max = 50;
		System.out.println("Even No from 1 to" + Max + ":");
		for(int i =2; i<=Max ; i++) 
		{
			if(i % 2 == 0)
			{
				System.out.println("i");
			}
		}
	}

}
